CREATE TABLE ${regionesTable}(${regionId} int NOT NULL DEFAULT '0', ${regionName} varchar(50) DEFAULT NULL,PRIMARY KEY (${regionId}) );
CREATE TABLE ${provinciasTable} (${provinciaId} int NOT NULL DEFAULT '0', ${provinciaName} varchar(23) DEFAULT NULL, PRIMARY KEY (${provinciaId}), ${provinciaRegionId} int FOREIGN KEY REFERENCES ${regionesTable}(${regionId}) );
CREATE TABLE ${comunasTable} (${comunaId} int NOT NULL DEFAULT '0', ${comunaName} varchar(20) DEFAULT NULL, ${comunaProvinciaId} int FOREIGN KEY REFERENCES ${provinciasTable} (${provinciaId}), PRIMARY KEY (${comunaId}) );
